import React from "react";
import { useState } from "react";

const Todo = ({ description, done }) => {
  const [isDone, setDone] = useState(done);

  const changeTodo = () => {
    if (isDone) {
      setDone(false);
    } else {
      setDone(true);
    }
    console.log(done);
  };

  return (
    <div>
      <div
        className={
          done
            ? "flex justify-between items-center p-2 bg-green-600"
            : "flex justify-between items-center p-2 bg-red-600 "
        }
      >
        <h2 className="text-lg cursor-pointer" onClick={changeTodo}>
          {description}
        </h2>
        <button className="text-lg bg-gray-400 p-2 text-white">LÃ¶schen</button>
      </div>
    </div>
  );
};

export default Todo;
